package test;

import java.io.*;
import java.util.Base64;

import com.itextpdf.text.*;
import com.itextpdf.text.pdf.*;
import com.itextpdf.tool.xml.*;
import com.itextpdf.tool.xml.css.StyleAttrCSSResolver;
import com.itextpdf.tool.xml.html.CssAppliers;
import com.itextpdf.tool.xml.html.CssAppliersImpl;
import com.itextpdf.tool.xml.html.Tags;
import com.itextpdf.tool.xml.parser.XMLParser;
import com.itextpdf.tool.xml.pipeline.css.CssResolverPipeline;
import com.itextpdf.tool.xml.pipeline.html.*;
import com.itextpdf.tool.xml.pipeline.end.*;

public class HtmlToPdfConverter {

    public static void convertHtmlToPdf(String html, String pdfFilePath) throws Exception {
        OutputStream os = new FileOutputStream(pdfFilePath);
        Document document = new Document(PageSize.A4);
        PdfWriter writer = PdfWriter.getInstance(document, os);
        document.open();

        XMLWorkerHelper worker = XMLWorkerHelper.getInstance();
        CssAppliers cssAppliers = new CssAppliersImpl(new XMLWorkerFontProvider());
        HtmlPipelineContext htmlContext = new HtmlPipelineContext(cssAppliers);
        htmlContext.setTagFactory(Tags.getHtmlTagProcessorFactory());
        htmlContext.autoBookmark(false);

        // 自定义图像解析器
        ImageProvider imageProvider = new Base64ImageProvider();
        htmlContext.setImageProvider(imageProvider);

        PdfWriterPipeline pdf = new PdfWriterPipeline(document, writer);
        HtmlPipeline htmlPipeline = new HtmlPipeline(htmlContext, pdf);
        Pipeline<?> pipeline = new CssResolverPipeline(new StyleAttrCSSResolver(), htmlPipeline);

        XMLWorker workerInstance = new XMLWorker(pipeline, true);
        XMLParser parser = new XMLParser(workerInstance);
        parser.parse(new ByteArrayInputStream(html.getBytes()));

        document.close();
        os.close();
    }

    /**
     * Base64编码的图像提供程序
     */
    private static class Base64ImageProvider extends AbstractImageProvider {

        @Override
        public Image retrieve(String src) {
            if (src.startsWith("data:image/")) {
                try {
                    // 将Base64编码转换为字节数组
                    String base64 = src.split(",")[1];
                    byte[] bytes = Base64.getDecoder().decode(base64);

                    // 创建Image对象并返回
                    return Image.getInstance(bytes);
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
            return super.retrieve(src);
        }

        @Override
        public String getImageRootPath() {
            return null;
        }
    }

    public static void main(String[] args) {
        String t="<!DOCTYPE html>\n" +
                "<html lang=\"en\">\n" +
                "<head>\n" +
                "    <title>A simple HTML document</title>\n" +
                "</head>\n" +
                "<body>\n" +
                "    <p>Hello World!<p>\n" +
                "        <img src=\"data:image/png;base64,\n" +
                "\"/>\n" +
                "</body>\n" +
                "</html>";
        try {
            convertHtmlToPdf(t,"D:/AA.pdf");
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
